import React,{Component} from 'react';
import './App.scss';
import Menu from './Menu'
import Home from './Home'
import About from './About'
import Contact from './Contact'
import {Route,Link,NavLink,Switch,BrowserRouter as Router} from 'react-router-dom'
class App extends Component{
  constructor(){
    super();
    this.state = {
      isMenuOn : false
    }
  }
  menuClick = () =>{

    let btn = document.getElementById('btn')

    if(this.state.isMenuOn === true){
      btn.classList.add('close')
      this.setState({
        isMenuOn : false
      })
    }
    else{
      this.setState({
        isMenuOn : true
      })
    }

  }
  componentDidUpdate(){
    console.log('updated: ' ,this.state.isMenuOn)
  }
  componentDidMount(){
    console.log(this.state.isMenuOn)
  }
  render(){

    return(

       <Router>
         <React.Fragment>
        <div className="menu-btn" id="btn" onClick={this.menuClick}>
            <div className="menu-btn-line"></div>
            <div className="menu-btn-line"></div>
            <div className="menu-btn-line"></div>
        </div>
        <Menu menuScale={this.state.isMenuOn} />
       <Switch>
           <Route exact path="/home" component={Home} />
           <Route exact path="/about" component={About} />
           <Route exact path="/contact" component={Contact} />
         </Switch>
         </React.Fragment>
       </Router>
    )
  }
}
export default App;
