import React,{Component} from 'react';
import './App.scss';
import Menu from './Menu'
import Home from './Home'
import About from './About'
import Contact from './Contact'
import Works from './Works'
import Particles from 'react-particles-js';
import videoBg from './assets/img/videoplayback.mp4'
import {Route,Link,NavLink,Switch,BrowserRouter as Router} from 'react-router-dom'
class App extends Component{
  constructor(){
    super();
    this.state = {
      isMenuOn : false
    }
  }
  navlinkHandle = () =>{
    this.setState({
      isMenuOn : false
    })
    console.log('handle', this.state.isMenuOn)
    let btn = document.getElementById('btn')

    if(this.state.isMenuOn === true){
      btn.classList.remove('close')
      this.setState({
        isMenuOn : false
      })
    }
    else{
      btn.classList.add('close')
      this.setState({
        isMenuOn : true
      })
    }
  }
  menuClick = () =>{

    let btn = document.getElementById('btn')

    if(this.state.isMenuOn === true){
      btn.classList.remove('close')
      this.setState({
        isMenuOn : false
      })
    }
    else{
      btn.classList.add('close')
      this.setState({
        isMenuOn : true
      })
    }

  }

  render(){

    return(

       <Router>
         <React.Fragment>

              <div className="overlay">
           </div>
           <Particles className="Particles"
                params={{
                  particles: {
                    number: {
                      value: 8000,
                      density: {
                        enable: true,
                        value_area: 800
                      }
                    },
                    line_linked: {
                      shadow: {
                        enable: true,
                        color: "#fff",
                        blur: 0
                      }
                    }
                  }
                }}
                style={{
                  width: '100%',

                }} />



        <div className="menu-btn" id="btn" onClick={this.menuClick}>
            <div className="menu-btn-line"></div>
            <div className="menu-btn-line"></div>
            <div className="menu-btn-line"></div>
        </div>
        <Menu menuScale={this.state.isMenuOn} NavlinkClicked={this.navlinkHandle} />
       <Switch>
           <Route exact path="/" component={Home} />
           <Route exact path="/about" component={About} />
           <Route exact path="/contact" component={Contact} />
           <Route exact path="/Works" component={Works} />

         </Switch>
         </React.Fragment>
       </Router>
    )
  }
}
export default App;
