import React,{Component} from 'react';
import './App.css';
import UUID from 'uuid/v1'
import Listrow from './Listrow'
class App extends Component{
  constructor(){
    super();
    this.state = {
      list : [],
      tempInput : ''
    }
  }
  handleSubmint = (e) =>{
    e.preventDefault();

    let newList = {
      key : UUID(),
      content : this.state.tempInput,
      checked : true
    }
    let tempList = []
    if(this.state.list){
      tempList = this.state.list
    }
    tempList.push(newList);
    this.setState({
      list : tempList ,
      tempInput : ''
    })
  }
  handleChange = (e) =>{
   let val = e.target.value;
    this.setState({
      tempInput : val
    })

  }

  render(){
    const list =this.state.list.map((row)=>{
      return <Listrow content={this.state.list} checked={row.checked}/>
    })
    return(
      <React.Fragment>
         <form onSubmit={this.handleSubmint}>
      <input type="text" value={this.state.tempInput} onChange={this.handleChange} id=""/>
      <button type="submit">اضافه کن</button>
    </form>

      </React.Fragment>
    )
  }
}
export default App;
