import React,{Component} from 'react';
class Comp extends Component{
    constructor(){
        
        super();
        this.state = {
            color : 'red'
        }
    }
    

    func = () => {
        alert(input1.value)
    }

    render(){
    
   return  (
            <div>
          <input id="inp1" type="text" placeholder="write something here !"/>
            <button onClick={this.func}>click</button>
            </div>
        
    )
   
}
}

export default Comp;
