import React,{Component} from 'react';
import './App.scss';
import Menu from './Menu'
import Home from './Home'
import About from './About'
import Contact from './Contact'
import Works from './Works'
import Particles from 'react-particles-js';
import videoBg from './assets/img/videoplayback.mp4'
import {Route,Link,NavLink,Switch,BrowserRouter as Router} from 'react-router-dom'
class App extends Component{
  constructor(){
    super();
    this.state = {
      isMenuOn : false
    }
  }
  navlinkHandle = () =>{
    this.setState({
      isMenuOn : false
    })
    console.log('handle', this.state.isMenuOn)
    let btn = document.getElementById('btn')

    if(this.state.isMenuOn === true){
      btn.classList.remove('close')
      this.setState({
        isMenuOn : false
      })
    }
    else{
      btn.classList.add('close')
      this.setState({
        isMenuOn : true
      })
    }
  }
  menuClick = () =>{

    let btn = document.getElementById('btn')

    if(this.state.isMenuOn === true){
      btn.classList.remove('close')
      this.setState({
        isMenuOn : false
      })
    }
    else{
      btn.classList.add('close')
      this.setState({
        isMenuOn : true
      })
    }

  }

  render(){
    let config = {
      num: [40, 70],
      rps: 10000,
      radius: [5, 40],
      life: [1.5, 3],
      v: [2, 3],
      tha: [-40, 40],
      // body: "./img/icon.png", // Whether to render pictures
      // rotate: [0, 20],
      alpha: [0.6, 0],
      scale: [1, 0.1],
      position: "center", // all or center or {x:1,y:1,width:100,height:100}
      color: ["random", "#ff0000"],
      cross: "dead", // cross or bround
      random: 15,  // or null,
      g: 5,    // gravity
      // f: [2, -1], // force
      onParticleUpdate: (ctx, particle) => {
          ctx.beginPath();
          ctx.rect(particle.p.x, particle.p.y, particle.radius * 2, particle.radius * 2);
          ctx.fillStyle = particle.color;
          ctx.fill();
          ctx.closePath();
      }
    };

    return(

       <Router>
         <React.Fragment>

              <div className="overlay">
           </div>
           <Particles config={config} className="Particles"
                />



        <div className="menu-btn" id="btn" onClick={this.menuClick}>
            <div className="menu-btn-line"></div>
            <div className="menu-btn-line"></div>
            <div className="menu-btn-line"></div>
        </div>
        <Menu menuScale={this.state.isMenuOn} NavlinkClicked={this.navlinkHandle} />
       <Switch>
           <Route exact path="/" component={Home} />
           <Route exact path="/about" component={About} />
           <Route exact path="/contact" component={Contact} />
           <Route exact path="/Works" component={Works} />

         </Switch>
         </React.Fragment>
       </Router>
    )
  }
}
export default App;
