import React,{Component} from 'react';
import {Route,Link,NavLink,Switch,BrowserRouter as Router} from 'react-router-dom'
import './Menu.scss'
import Me from './assets/img/me.jpg'
class Menu extends Component {
    constructor(props){
        super(props)
    }
    componentWillReceiveProps(){
        if(this.props.menuScale === false){
            document.getElementById('menuContainer').classList.add('menu-show')
        }
        else{
            document.getElementById('menuContainer').classList.remove('menu-show')
        }
    }
    render(){

        return(
          <header id="menuContainer">
              
              <div className="header-btns">
                  <Router>
                  <ul>
                      <li>
                      <NavLink to="/" className="menu-item">خانه</NavLink >
                      </li>
                      <li>
                      <a href="#" className="menu-item">درباره</a>

                      </li>
                      <li>
                      <a href="#" className="menu-item">نمونه</a>

                      </li>
                      <li>
                          <a href="#" className="menu-item">ارتباط</a>
                      </li>
                  </ul>
                  </Router>
                  
              </div>
              <div className="header-img">
                  <img src={Me} />
              </div>
          </header>
        )
    }
}
export default Menu
