import './App.css';
import UUID from 'uuid/v1'
class App extends Component{
  constructor(){
    super();
    this.state = {
      list : [],
      tempInput : ''
    }
  }
  handleSubmint = (e) =>{
    e.preventDefault();
    if(this.state.list === ''){
      alert("sda")
    }
  }
  handleChange = (e) =>{
   let val = e.target.value;
    this.setState({
      tempInput : val
    })
  }
  render(){
    return(
    <form onSubmit={this.handleSubmint}>
      <input type="text" value={this.state.tempInput} onChange={this.handleChange} id=""/>
      <button type="submit">اضافه کن</button>
    </form>

    )
  }
}
export default App;
