import React,{Component} from 'react';
import './App.scss';
import Menu from './Menu'
class App extends Component{
  constructor(){
    super();
    this.state = {
      isMenuOn : false
    }
  }
  menuClick = () =>{

    let btn = document.getElementById('btn')
    btn.classList.toggle('close')
    if(this.state.isMenuOn === true){
      this.setState({
        isMenuOn : false
      })
    }
    else{
      this.setState({
        isMenuOn : true
      })
    }

  }
  componentDidUpdate(){
    console.log(this.state.isMenuOn)
  }
  componentDidMount(){
    console.log(this.state.isMenuOn)
  }
  render(){

    return(
     <React.Fragment>
        <div className="menu-btn" id="btn" onClick={this.menuClick}>
            <div className="menu-btn-line"></div>
            <div className="menu-btn-line"></div>
            <div className="menu-btn-line"></div>
        </div>
        <Menu menuScale={this.state.isMenuOn} />
     </React.Fragment>
    )
  }
}
export default App;
