import React,{Component} from 'react';
class Comp extends Component{
    constructor(){
        
        super();
        this.state = {
            color : 'red'
        }
    }
    

    func = () => {
        let val = document.getElementById('inp1').value;
        document.getElementById('p').innerHTML = val;
    }

    render(){
    
   return  (
            <div>
          <input  onChange={this.func} id="inp1" type="text" placeholder="write something here !"/>
           <p id="p"></p>
            </div>
        
    )
   
}
}

export default Comp;
